import React, { Fragment, useState } from "react";
import fs from "fs";
import axios from "axios";

const Upload = () => {
  const [file, setFile] = useState("");
  const [fileName, setFileName] = useState("Choose File");
  const [uploadedFile, setUploadedFile] = useState({});

  // const logPath = path.join(__dirname,'logs/errorlogs.txt')

  const onChange = (event) => {
    setFile(event.target.files[0]);
    setFileName(event.target.files[0].name);
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((x)=> {
        console.log(x);
      })
      // .catch (err => {
      //   fs.write(`${__dirname}/errFile.txt`, err)
      //   console.log('.catch error', err)
      // })
      // console.log('res is ',res);
      // const { fileName, filePath } = res.data;
      // setUploadedFile({ fileName, filePath });
    } catch (err) {
      console.log(err)
      if (err.response.status === 500) {
        console.error("There was an error with the server");
      } else {
        console.error('this is error else ',err.response.msg);
      }
    }
  };

  return (
    <Fragment>
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <input
            className="form-control"
            type="file"
            id="formFile"
            onChange={onChange}
          />
        </div>
        <input
          type="submit"
          value="Upload"
          className="btn btn-primary btn-block mt-4"
        />
      </form>
      {(uploadedFile.length > 0) ?
      (<div>
        <h2>File Uploaded Successfully</h2>
      </div>)
      : null}
    </Fragment>
  );
};

export default Upload;
