import React, { Fragment, useState } from "react";
// import fs from "fs";
import axios from "axios";

const Upload = () => {
  const [file, setFile] = useState("");
  const [report, setReport] = useState(null);

  // const logPath = path.join(__dirname,'logs/errorlogs.txt')

  const onChange = (event) => {
    setFile(event.target.files[0]);
    // setFileName(event.target.files[0].name);
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      const report = res.data;
      console.log(report)
      setReport(report);
    } catch (err) {
      console.log(err)
      if (err.response.status === 500) {
        console.error("There was an error with the server");
      } else {
        console.error('this is error else ',err.response.msg);
      }
    }
  };

  return (
    <Fragment>
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <input
            className="form-control"
            type="file"
            id="formFile"
            onChange={onChange}
          />
        </div>
        <input
          type="submit"
          value="Upload"
          className="btn btn-primary btn-block mt-4"
        />
      </form>
      { report ?
      (<div>
        <h2>File Uploaded Successfully</h2>
        <p>Report: {report} </p>
      </div>)
      : <h2> Please select a script file to upload</h2>}
    </Fragment>
  );
};

export default Upload;
